import { Avatar } from 'antd';
import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
const ActiveUsers = () => {
    const activeUsers = useSelector(state => state. AllUserReducer.activeUsers)
    const User = useSelector((state) => state.UserReducers.user);
    
    const friendsId = User.friends.map((id) => id);
    const ActiveFriends = activeUsers.filter(user => friendsId.includes(user._id))
    const excludedLoggedInuser = ActiveFriends.filter(user => user._id !== User._id )
   
  return (
    <div>
        <div className='mt-[10px]'>
        <h3 className='text-[17px] roboto-medium'>Currently active Friends</h3>
            {excludedLoggedInuser.length ? excludedLoggedInuser.map(user => (
                <div key={user._id}>
                <div className="flex justify-between">
                  <div className="flex gap-x-[7px] items-center">
                    <Link to={`/user/${user._id}`}>
                    <Avatar
                      src={
                        user.profilePic
                          ? user.profilePic
                          : "data:image/png;base64,"
                      }
                      size={50}
                      className=' border-green-600 border-[5px]'
                    />
                    </Link>
                    <p className="roboto-bold text-[20px] text-black">
                      {user.username}
                    </p>
                  </div>

                  <span
                    className="bg-black w-fit p-[7px] rounded-[8px] h-[40px] cursor-pointer text-white text-[18px] roboto-medium"
                  >
                    Message
                  </span>
                </div>
              </div>
            )): <p>No Active Friends</p>}
        </div>
    </div>
  )
}

export default ActiveUsers